import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls: ['./landing.component.css']
})
export class LandingComponent {

  authType: string = 'login';
  changeAuthType(type: string){
    this.authType = type;
  }


  username:string = '';
  email: string = '';
  password: string ='';
  usertype: string = '';
  details: {} = {};

  constructor(private http: HttpClient, private route: Router){ }

  ngOnInit(): void {

    const userid = localStorage.getItem('userid');
    const userType = localStorage.getItem('usertype');

    if(userid && userType === 'admin'){
      this.route.navigate(['/admin']);
    } else if(userid && userType === 'customer'){
      this.route.navigate(['/home']);
    }
  }

  register(){

    this.details = {username: this.username, email: this.email, usertype: this.usertype, password: this.password};
    this.http.post('http://localhost:6001/register', this.details).subscribe(
        (response:any) =>{
          localStorage.setItem('userid', response._id);
          localStorage.setItem('username', response.username);
          localStorage.setItem('email', response.email);
          localStorage.setItem('usertype', response.usertype);
          this.username = '';
          this.email = '';
          this.password='';
          this.usertype = '';

          if (response.usertype === 'customer'){

            this.route.navigate(['/home']);

          }else if(response.usertype === 'admin'){

            this.route.navigate(['/admin']);

          }

        },
        (error) => {
          console.error(error);
        }
    )
  }

  login(){
    this.details = {email: this.email, usertype: this.usertype, password: this.password};

    this.http.post('http://localhost:6001/login', this.details).subscribe(
        (response:any) =>{
          localStorage.setItem('userid', response._id);
          localStorage.setItem('username', response.username);
          localStorage.setItem('email', response.email);
          localStorage.setItem('usertype', response.usertype);
          this.username = '';
          this.email = '';
          this.password='';
          this.usertype = '';

          if (response.usertype === 'customer'){

            this.route.navigate(['/home']);

          }else if(response.usertype === 'admin'){

            this.route.navigate(['/admin']);

          }
        },
        (error) => {
          console.error(error);
        }
    )
  }


}

<div class="landing-page" id="home">

    <nav>
        <h3>SB Bank</h3>
        <div class="nav-options">
            <a href="#home">Home</a>
            <a href="#about">About</a>
            <a href="#joinNow">Join now</a>
        </div>
    </nav>

    <div class="landing-hero">

        <div class="titles">
            <h1>Experience Secure and Convenient Banking Services</h1>
            <p>Simplify your financial management with our intuitive banking app, designed to provide seamless access to all your accounts, transactions, and services.</p>
        </div>

    </div>

    <div class="about" id="about">
        <h2>About</h2>
        <div class="about-body">
            <div class="about-data">
                <p>&nbsp; &nbsp; &nbsp; &nbsp; The <b>SB Bank</b> is your dedicated financial partner, prioritizing your needs and delivering exceptional banking services. We offer a comprehensive range of convenient and flexible solutions to simplify your financial journey. Your security and trust are paramount, as we employ advanced encryption technology to protect your information. Our experienced team is committed to personalized service and prompt assistance. We also actively contribute to community initiatives, making a positive impact. Explore our website for a modern banking experience with the SB Bank.</p>
                <br>
                <p>&nbsp; &nbsp; &nbsp; &nbsp; At <b>SB Bank</b>, your security and trust are our utmost priorities. We have implemented state-of-the-art security measures to ensure the confidentiality and integrity of your personal and financial information. From robust encryption technology to rigorous privacy standards, we leave no stone unturned in protecting your data. Rest assured that your financial transactions and interactions with our bank are conducted in a secure and protected environment.</p>
            </div>
            <div class="Authentication" id="joinNow">

                <div  [ngClass]="{ 'hide': authType !== 'login', 'Auth': authType === 'login' }">
                    <form>
                        <h3>Login</h3>
                        <div class="mb-3">
                          <label for="exampleInputEmail1" class="form-label">Email address</label>
                          <input type="email" class="form-control " id="exampleInputEmail1" aria-describedby="emailHelp" name="email" [(ngModel)]="email">
                        </div>
                        <div class="mb-3">
                          <label for="exampleInputPassword1" class="form-label">Password</label>
                          <input type="password" class="form-control" id="exampleInputPassword1" name="password" [(ngModel)]="password">
                        </div>
                        <div class="mb-3">
                            <label for="loginTypeSelect" class="form-label">User Type</label>
                            <select class="form-select" id="loginTypeSelect" aria-label="Default select example" name="usertype" [(ngModel)]="usertype">
                                <option selected></option>
                                <option value="customer">Customer</option>
